const togglemeny=()=>{document.querySelectorAll(".burger").forEach(e=>{e.classList.toggle("active")}),document.querySelector(".menu").classList.toggle("active")},sliderNext=e=>{let t=document.querySelector(".slider"),l=document.querySelectorAll(".slider-button");if("left"!=e||"right"!=e){t.style.transform=`translateX(-${100*e}%)`;for(let t=0;t<l.length;t++)l[t].classList.remove("active"),t==e&&l[t].classList.add("active")}else if(alert(1),"left"==e)for(let e=0;e<l.length;e++)if(l[e].classList.contains("active"))if(e+1<l.length){t.style.transform=`translateX(-${100*e+1}%)`;for(let t=0;t<l.length;t++)l[t].classList.remove("active"),t==e+1&&l[t].classList.add("active")}else{t.style.transform="translateX(-0%)";for(let e=0;e<l.length;e++)l[e].classList.remove("active"),0==e&&l[e].classList.add("active")}};setInterval(()=>{let e=document.querySelectorAll(".slider-button");for(let t=0;t<e.length;t++)if(e[t].classList.contains("active")){t==e.length-1?sliderNext(0):sliderNext(t+1);break}},5e3);




